DROP TABLE BUSINESS;

CREATE TABLE BUSINESS(
	CUST_ENTRY_NO 			VARCHAR(14)		NOT NULL,
	BUSINESS_DATE			VARCHAR(10)   	NOT NULL,
	CUST_ID					VARCHAR(14)		NOT NULL,
	CUST_IN_TIME			TIMESTAMP,
