	EMP_ID					VARCHAR(14),
	CREATION_TIME			TIMESTAMP,
	MODIFICATION_TIME		TIMESTAMP
	);

ALTER TABLE BUSINESS ADD CONSTRAINT BUSINESS_PK PRIMARY KEY (CUST_ENTRY_NO, BUSINESS_DATE, CUST_ID);

